var video = document.getElementById("vid");
var audioText = document.getElementById("audioButton");
var videoText = document.getElementById("videoButton");

video.volume = 0.1;


function videoPlay() {
if (video.paused) {
    video.play();
    videoText.innerHTML = "pause video";
  } else {
    video.pause();
    videoText.innerHTML = "resume video";
  }
}

function audioPlay() {
 if (video.volume == 0) {
  video.volume = 0.1;
  audioText.innerHTML = "mute video";
} else {
  video.volume = 0;
  audioText.innerHTML = "unmute video";
}
}

var mobileSrc = "https://files.catbox.moe/yc6bv2.mp4";
var desktopSrc = "https://files.catbox.moe/4jg9fe.mp4";

var isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent);
var sourcevid = document.getElementById('sourcevid');
var element = document.querySelector('.font-right-bottom');

if (isMobile) {
    sourcevid.src = mobileSrc;
    element.style.display = 'none';
} else {
    sourcevid.src = desktopSrc;
}

video.loop = true;
video.load();

